(()=>{const e=[{description:"Go grocery shopping",completed:!1,index:1},{description:"Finish homework",completed:!0,index:2},{description:"Take out the trash",completed:!1,index:3}];!function(){const t=document.getElementById("task-list");t.innerHTML="",e.forEach((e=>{const n=function(e){const t=document.createElement("li");return t.innerHTML=`\n    <div class="task-item">\n      <div class="task-item__checkbox">\n        <input type="checkbox" id="task-${e.index}" ${e.completed?"checked":""}>\n        <label for="task-${e.index}"></label>\n      </div>\n      <div class="task-item__description ${e.completed?"completed":""}">\n        ${e.description}\n      </div>\n      <div class="task-item__delete">\n        <button class="delete-button" data-index="${e.index}">\n          <i class="fas fa-trash-alt"></i>\n        </button>\n      </div>\n    </div>\n  `,t}(e);t.appendChild(n)}))}()})();